import React, {Component, useState} from 'react';
import './App.css';
import Person from './Components/Person/Person';

class App extends Component {
  state = {
    applications: [
      { name: "Login system", language: 'C++' },
      { name: "employement system", language: "Java" },
      { name: "Theme builder", language: "React", days: "30" }
    ]
  }

  changeDaysHandler = () => {
    this.setState({
      applications: [
        { name: "Login system", language: 'C++' },
        { name: "employement system", language: "Java" },
        { name: "Theme builder", language: "React", days: Math.floor(Math.random() * 20)}
      ]
    })
  }

  render() {

    const [expertState, setExpertState] = useState({
      techExperts: [
        {name: 'Max', experiance: '3yrs'},
        {name: 'Lina', experiance: '7yrs'},
        {name: 'Joe', experiance: '5yrs'}
      ]
    });

    return (
      <div className="app">
        <h1>Hi, I am React App</h1>
        <p>This is great!!!</p>
        <Person name="max">I am loving it</Person>
        <Person name="Mini">
          <div>
            <h3>I am learning react</h3>
            <div>
              <p>This is a lesson about props and state. I am gonna create project based on <span style={{color: "red"}}> {this.state.applications[2].language} </span> and my application name is <span style={{color: "red"}}> {this.state.applications[2].name} </span> within <span style={{color: "red"}}> {this.state.applications[2].days}</span> days </p>
              <button onClick={this.changeDaysHandler}>Change Days</button>
              <p>We have experts for working on this </p>
            </div>
          </div>
        </Person>
      </div>
    );
  }
}

export default App;
