import React, {Component} from 'react';
import './App.css';
import Person from './Components/Person/Person';
import './Components/Person/style.css';

class App extends Component {
  state = {
    applications: [
      { name: "Login system", language: 'C++' },
      { name: "employement system", language: "Java" },
      { name: "Theme builder", language: "React", days: "30" }
    ],
    showPerson: false
  }

  changeDaysHandler = (newDay) => {
    this.setState({
      applications: [
        { name: "Login system", language: 'C++' },
        { name: "employement system", language: "Java" },
        { name: "Theme builder", language: "React", days: newDay}
      ]
    })
  }

  changeNameHandler = (event) => {
    this.setState({
      applications: [
        { name: "Login system", language: 'C++' },
        { name: "employement system", language: "Java" },
        { name: event.target.value, language: "React", days: "52"}
      ]
    })
  }

  toggleSwitchHandler = (event)  => {
    const doesShow = this.state.showPerson;
    console.log("am I visible " + doesShow);
    this.setState({showPerson : !doesShow});
  }


  render() {

    return (
      <div className="app">
        <h1>Hi, I am React App</h1>
        <p>This is great!!!</p>
        <button onClick={this.toggleSwitchHandler}>Show Person Info</button>
        {this.state.showPerson ?
          <div>
          <Person className="Person1" name="max">I am loving it</Person>

        </div> : <div><p>Nothing to show. Please click above button show details</p></div>
        }
      </div>
    );
  }
}

export default App;
